	(6, '联系方式', 0, 1, 6, 0, '6', 'c_spage.php', 'c_mspage.php', 'd_normal.php', 'd_mnormal.php', '<p style="white-space:normal;">\r\n	联系人：\r\n</p>\r\n<p style="white-space:normal;">\r\n	电话：\r\n</p>\r\n<p style="white-space:normal;">\r\n	手机：\r\n</p>\r\n<p style="white-space:normal;">\r\n	微信：\r\n</p>\r\n<p style="white-space:normal;">\r\n	QQ：\r\n</p>\r\n<p style="white-space:normal;">\r\n	邮箱：\r\n</p>\r\n<p style="white-space:normal;">\r\n	地址：\r\n</p>', '<p>\r\n	联系人：\r\n</p>\r\n<p>\r\n	电话：\r\n</p>\r\n<p>\r\n	手机：\r\n</p>\r\n<p>\r\n	微信：\r\n</p>\r\n<p>\r\n	QQ：\r\n</p>\r\n<p>\r\n	邮箱：\r\n</p>\r\n<p>\r\n	地址：\r\n</p>', '', '', 20, '', '', '', 1, '联系方式', '', '文章', 'Contact', '', '', '_self', 1, 6, 0),
