  `f_content` longtext,
  `f_answer` longtext,
  `f_date` int(11) DEFAULT NULL,
  `f_adate` int(11) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;

