INSERT INTO `proc` (`db`, `name`, `type`, `specific_name`, `language`, `sql_data_access`, `is_deterministic`, `security_type`, `param_list`, `returns`, `body`, `definer`, `created`, `modified`, `sql_mode`, `comment`, `character_set_client`, `collation_connection`, `db_collation`, `body_utf8`) VALUES
	('sys', 'extract_schema_from_file_name', 'FUNCTION', 'extract_schema_from_file_name', 'SQL', 'NO_SQL', 'YES', 'INVOKER', _binary 0x20706174682056415243484152283531322920, _binary 0x766172636861722836342920434841525345542075746638, _binary 0x424547494e2052455455524e204c45465428535542535452494e475f494e44455828535542535452494e475f494e444558285245504c41434528706174682c20275c5c272c20272f27292c20272f272c202d32292c20272f272c2031292c203634293b20454e44, 'mysql.sys@localhost', '2021-12-25 09:51:02', '2021-12-25 09:51:02', '', '\n Description\n \n Takes a raw file path, and attempts to extract the schema name from it.\n \n Useful for when interacting with Performance Schema data \n concerning IO statistics, for example.\n \n Currently relies on the fact that a table data file will be within a \n specified database directory (will not work with partitions or tables\n that specify an individual DATA_DIRECTORY).\n \n Parameters\n \n path (VARCHAR(512)):\n The full file path to a data file to extract the schema name from.\n \n Returns\n \n VARCHAR(64)\n \n Example\n \n mysql> SELECT sys.extract_schema_from_file_name(\'/var/lib/mysql/employees/employee.ibd\');\n +----------------------------------------------------------------------------+\n | sys.extract_schema_from_file_name(\'/var/lib/mysql/employees/employee.ibd\') |\n +----------------------------------------------------------------------------+\n | employees                                                                  |\n +----------------------------------------------------------------------------+\n 1 row in set (0.00 sec)\n ', 'utf8', 'utf8_general_ci', 'utf8_general_ci', _binary 0x424547494e2052455455524e204c45465428535542535452494e475f494e44455828535542535452494e475f494e444558285245504c41434528706174682c20275c272c20272f27292c20272f272c202d32292c20272f272c2031292c203634293b20454e44),
	('sys', 'extract_table_from_file_name', 'FUNCTION', 'extract_table_from_file_name', 'SQL', 'NO_SQL', 'YES', 'INVOKER', _binary 0x20706174682056415243484152283531322920, _binary 0x766172636861722836342920434841525345542075746638, _binary 0x424547494e2052455455524e204c45465428535542535452494e475f494e444558285245504c41434528535542535452494e475f494e444558285245504c41434528706174682c20275c5c272c20272f27292c20272f272c202d31292c20274030303234272c20272427292c20272e272c2031292c203634293b20454e44, 'mysql.sys@localhost', '2021-12-25 09:51:02', '2021-12-25 09:51:02', '', '\n Description\n \n Takes a raw file path, and extracts the table name from it.\n \n Useful for when interacting with Performance Schema data \n concerning IO statistics, for example.\n \n Parameters\n \n path (VARCHAR(512)):\n The full file path to a data file to extract the table name from.\n \n Returns\n \n VARCHAR(64)\n \n Example\n \n mysql> SELECT sys.extract_table_from_file_name(\'/var/lib/mysql/employees/employee.ibd\');\n +---------------------------------------------------------------------------+\n | sys.extract_table_from_file_name(\'/var/lib/mysql/employees/employee.ibd\') |\n +---------------------------------------------------------------------------+\n | employee                                                                  |\n +---------------------------------------------------------------------------+\n 1 row in set (0.02 sec)\n ', 'utf8', 'utf8_general_ci', 'utf8_general_ci', _binary 0x424547494e2052455455524e204c45465428535542535452494e475f494e444558285245504c41434528535542535452494e475f494e444558285245504c41434528706174682c20275c272c20272f27292c20272f272c202d31292c20274030303234272c20272427292c20272e272c2031292c203634293b20454e44),
	('sys', 'format_bytes', 'FUNCTION', 'format_bytes', 'SQL', 'NO_SQL', 'YES', 'INVOKER', _binary 0x206279746573205445585420, _binary 0x7465787420434841525345542075746638, _binary , 'mysql.sys@localhost', '2021-12-25 09:51:02', '2021-12-25 09:51:02', '', '\n Description\n \n Takes a raw bytes value, and converts it to a human readable format.\n \n Parameters\n \n bytes (TEXT):\n A raw bytes value.\n \n Returns\n \n TEXT\n \n Example\n \n mysql> SELECT sys.format_bytes(2348723492723746) AS size;\n +----------+\n | size     |\n +----------+\n | 2.09 PiB |\n +----------+\n 1 row in set (0.00 sec)\n \n mysql> SELECT sys.format_bytes(2348723492723) AS size;\n +----------+\n | size     |\n +----------+\n | 2.14 TiB |\n +----------+\n 1 row in set (0.00 sec)\n \n mysql> SELECT sys.format_bytes(23487234) AS size;\n +-----------+\n | size      |\n +-----------+\n | 22.40 MiB |\n +-----------+\n 1 row in set (0.00 sec)\n ', 'utf8', 'utf8_general_ci', 'utf8_general_ci', _binary ),
	('sys', 'format_path', 'FUNCTION', 'format_path', 'SQL', 'NO_SQL', 'YES', 'INVOKER', _binary 0x20696e5f706174682056415243484152283531322920, _binary 0x76617263686172283531322920434841525345542075746638, _binary , 'mysql.sys@localhost', '2021-12-25 09:51:02', '2021-12-25 09:51:02', '', '\n Description\n \n Takes a raw path value, and strips out the datadir or tmpdir\n replacing with @@datadir and @@tmpdir respectively.\n \n Also normalizes the paths across operating systems, so backslashes\n on Windows are converted to forward slashes\n \n Parameters\n \n path (VARCHAR(512)):\n The raw file path value to format.\n \n Returns\n \n VARCHAR(512) CHARSET UTF8\n \n Example\n \n mysql> select @@datadir;\n +-----------------------------------------------+\n | @@datadir                                     |\n +-----------------------------------------------+\n | /Users/mark/sandboxes/SmallTree/AMaster/data/ |\n +-----------------------------------------------+\n 1 row in set (0.06 sec)\n \n mysql> select format_path(\'/Users/mark/sandboxes/SmallTree/AMaster/data/mysql/proc.MYD\') AS path;\n +--------------------------+\n | path                     |\n +--------------------------+\n | @@datadir/mysql/proc.MYD |\n +--------------------------+\n 1 row in set (0.03 sec)\n ', 'utf8', 'utf8_general_ci', 'utf8_general_ci', _binary ),
	('sys', 'format_statement', 'FUNCTION', 'format_statement', 'SQL', 'NO_SQL', 'YES', 'INVOKER', _binary 0x2073746174656d656e74204c4f4e475445585420, _binary 0x6c6f6e677465787420434841525345542075746638, _binary , 'mysql.sys@localhost', '2021-12-25 09:51:02', '2021-12-25 09:51:02', '', '\n Description\n \n Formats a normalized statement, truncating it if it is > 64 characters long by default.\n \n To configure the length to truncate the statement to by default, update the `statement_truncate_len`\n variable with `sys_config` table to a different value. Alternatively, to change it just for just \n your particular session, use `SET @sys.statement_truncate_len := <some new value>`.\n \n Useful for printing statement related data from Performance Schema from \n the command line.\n \n Parameters\n \n statement (LONGTEXT): \n The statement to format.\n \n Returns\n \n LONGTEXT\n \n Example\n \n mysql> SELECT sys.format_statement(digest_text)\n ->   FROM performance_schema.events_statements_summary_by_digest\n ->  ORDER by sum_timer_wait DESC limit 5;\n +-------------------------------------------------------------------+\n | sys.format_statement(digest_text)                                 |\n +-------------------------------------------------------------------+\n | CREATE SQL SECURITY INVOKER VI ... KE ? AND `variable_value` > ?  |\n | CREATE SQL SECURITY INVOKER VI ... ait` IS NOT NULL , `esc` . ... |\n | CREATE SQL SECURITY INVOKER VI ... ait` IS NOT NULL , `sys` . ... |\n | CREATE SQL SECURITY INVOKER VI ...  , `compressed_size` ) ) DESC  |\n | CREATE SQL SECURITY INVOKER VI ... LIKE ? ORDER BY `timer_start`  |\n +-------------------------------------------------------------------+\n 5 rows in set (0.00 sec)\n ', 'utf8', 'utf8_general_ci', 'utf8_general_ci', _binary ),
	('sys', 'format_time', 'FUNCTION', 'format_time', 'SQL', 'NO_SQL', 'YES', 'INVOKER', _binary 0x207069636f7365636f6e6473205445585420, _binary 0x7465787420434841525345542075746638, _binary , 'mysql.sys@localhost', '2021-12-25 09:51:02', '2021-12-25 09:51:02', '', '\n Description\n \n Takes a raw picoseconds value, and converts it to a human readable form.\n \n Picoseconds are the precision that all latency values are printed in\n within Performance Schema, however are not user friendly when wanting\n to scan output from the command line.\n \n Parameters\n \n picoseconds (TEXT):\n The raw picoseconds value to convert.\n \n Returns\n \n TEXT\n \n Example\n \n mysql> select format_time(342342342342345);\n +------------------------------+\n | format_time(342342342342345) |\n +------------------------------+\n | 00:05:42                     |\n +------------------------------+\n 1 row in set (0.00 sec)\n \n mysql> select format_time(342342342);\n +------------------------+\n | format_time(342342342) |\n +------------------------+\n | 342.34 us              |\n +------------------------+\n 1 row in set (0.00 sec)\n \n mysql> select format_time(34234);\n +--------------------+\n | format_time(34234) |\n +--------------------+\n | 34.23 ns           |\n +--------------------+\n 1 row in set (0.00 sec)\n ', 'utf8', 'utf8_general_ci', 'utf8_general_ci', _binary ),
	('sys', 'list_add', 'FUNCTION', 'list_add', 'SQL', 'CONTAINS_SQL', 'YES', 'INVOKER', _binary 0x20696e5f6c69737420544558542c20696e5f6164645f76616c7565205445585420, _binary 0x7465787420434841525345542075746638, _binary , 'mysql.sys@localhost', '2021-12-25 09:51:02', '2021-12-25 09:51:02', '', '\n Description\n \n Takes a list, and a value to add to the list, and returns the resulting list.\n \n Useful for altering certain session variables, like sql_mode or optimizer_switch for instance.\n \n Parameters\n \n in_list (TEXT):\n The comma separated list to add a value to\n \n in_add_value (TEXT):\n The value to add to the input list\n \n Returns\n \n TEXT\n \n Example\n \n mysql> select @@sql_mode;\n +-----------------------------------------------------------------------------------+\n | @@sql_mode                                                                        |\n +-----------------------------------------------------------------------------------+\n | ONLY_FULL_GROUP_BY,STRICT_TRANS_TABLES,NO_AUTO_CREATE_USER,NO_ENGINE_SUBSTITUTION |\n +-----------------------------------------------------------------------------------+\n 1 row in set (0.00 sec)\n \n mysql> set sql_mode = sys.list_add(@@sql_mode, \'ANSI_QUOTES\');\n Query OK, 0 rows affected (0.06 sec)\n \n mysql> select @@sql_mode;\n +-----------------------------------------------------------------------------------------------+\n | @@sql_mode                                                                                    |\n +-----------------------------------------------------------------------------------------------+\n | ANSI_QUOTES,ONLY_FULL_GROUP_BY,STRICT_TRANS_TABLES,NO_AUTO_CREATE_USER,NO_ENGINE_SUBSTITUTION |\n +-----------------------------------------------------------------------------------------------+\n 1 row in set (0.00 sec)\n \n ', 'utf8', 'utf8_general_ci', 'utf8_general_ci', _binary ),
	('sys', 'list_drop', 'FUNCTION', 'list_drop', 'SQL', 'CONTAINS_SQL', 'YES', 'INVOKER', _binary 0x20696e5f6c69737420544558542c20696e5f64726f705f76616c7565205445585420, _binary 0x7465787420434841525345542075746638, _binary , 'mysql.sys@localhost', '2021-12-25 09:51:02', '2021-12-25 09:51:02', '', '\n Description\n \n Takes a list, and a value to attempt to remove from the list, and returns the resulting list.\n \n Useful for altering certain session variables, like sql_mode or optimizer_switch for instance.\n \n Parameters\n \n in_list (TEXT):\n The comma separated list to drop a value from\n \n in_drop_value (TEXT):\n The value to drop from the input list\n \n Returns\n \n TEXT\n \n Example\n \n mysql> select @@sql_mode;\n +-----------------------------------------------------------------------------------------------+\n | @@sql_mode                                                                                    |\n +-----------------------------------------------------------------------------------------------+\n | ANSI_QUOTES,ONLY_FULL_GROUP_BY,STRICT_TRANS_TABLES,NO_AUTO_CREATE_USER,NO_ENGINE_SUBSTITUTION |\n +-----------------------------------------------------------------------------------------------+\n 1 row in set (0.00 sec)\n \n mysql> set sql_mode = sys.list_drop(@@sql_mode, \'ONLY_FULL_GROUP_BY\');\n Query OK, 0 rows affected (0.03 sec)\n \n mysql> select @@sql_mode;\n +----------------------------------------------------------------------------+\n | @@sql_mode                                                                 |\n +----------------------------------------------------------------------------+\n | ANSI_QUOTES,STRICT_TRANS_TABLES,NO_AUTO_CREATE_USER,NO_ENGINE_SUBSTITUTION |\n +----------------------------------------------------------------------------+\n 1 row in set (0.00 sec)\n \n ', 'utf8', 'utf8_general_ci', 'utf8_general_ci', _binary ),
	('sys', 'ps_is_account_enabled', 'FUNCTION', 'ps_is_account_enabled', 'SQL', 'READS_SQL_DATA', 'YES', 'INVOKER', _binary 0x20696e5f686f73742056415243484152283630292c2020696e5f7573657220564152434841522833322920, _binary 0x656e756d2827594553272c274e4f272920434841525345542075746638, _binary , 'mysql.sys@localhost', '2021-12-25 09:51:02', '2021-12-25 09:51:02', '', '\n Description\n \n Determines whether instrumentation of an account is enabled \n within Performance Schema.\n \n Parameters\n \n in_host VARCHAR(60): \n The hostname of the account to check.\n in_user VARCHAR(32):\n The username of the account to check.\n \n Returns\n \n ENUM(\'YES\', \'NO\', \'PARTIAL\')\n \n Example\n \n mysql> SELECT sys.ps_is_account_enabled(\'localhost\', \'root\');\n +------------------------------------------------+\n | sys.ps_is_account_enabled(\'localhost\', \'root\') |\n +------------------------------------------------+\n | YES                                            |\n +------------------------------------------------+\n 1 row in set (0.01 sec)\n ', 'utf8', 'utf8_general_ci', 'utf8_general_ci', _binary ),
	('sys', 'ps_is_consumer_enabled', 'FUNCTION', 'ps_is_consumer_enabled', 'SQL', 'READS_SQL_DATA', 'YES', 'INVOKER', _binary 0x20696e5f636f6e73756d657220766172636861722836342920, _binary 0x656e756d2827594553272c274e4f272920434841525345542075746638, _binary , 'mysql.sys@localhost', '2021-12-25 09:51:02', '2021-12-25 09:51:02', '', '\n Description\n \n Determines whether a consumer is enabled (taking the consumer hierarchy into consideration)\n within the Performance Schema.\n \n An exception with errno 3047 is thrown if an unknown consumer name is passed to the function.\n A consumer name of NULL returns NULL.\n \n Parameters\n \n in_consumer VARCHAR(64): \n The name of the consumer to check.\n \n Returns\n \n ENUM(\'YES\', \'NO\')\n \n Example\n \n mysql> SELECT sys.ps_is_consumer_enabled(\'events_stages_history\');\n +-----------------------------------------------------+\n | sys.ps_is_consumer_enabled(\'events_stages_history\') |\n +-----------------------------------------------------+\n | NO                                                  |\n +-----------------------------------------------------+\n 1 row in set (0.00 sec)\n ', 'utf8', 'utf8_general_ci', 'utf8_general_ci', _binary ),
	('sys', 'ps_is_instrument_default_enabled', 'FUNCTION', 'ps_is_instrument_default_enabled', 'SQL', 'READS_SQL_DATA', 'YES', 'INVOKER', _binary 0x20696e5f696e737472756d656e742056415243484152283132382920, _binary 0x656e756d2827594553272c274e4f272920434841525345542075746638, _binary , 'mysql.sys@localhost', '2021-12-25 09:51:02', '2021-12-25 09:51:02', '', '\n Description\n \n Returns whether an instrument is enabled by default in this version of MySQL.\n \n Parameters\n \n in_instrument VARCHAR(128): \n The instrument to check.\n \n Returns\n \n ENUM(\'YES\', \'NO\')\n \n Example\n \n mysql> SELECT sys.ps_is_instrument_default_enabled(\'statement/sql/select\');\n +--------------------------------------------------------------+\n | sys.ps_is_instrument_default_enabled(\'statement/sql/select\') |\n +--------------------------------------------------------------+\n | YES                                                          |\n +--------------------------------------------------------------+\n 1 row in set (0.00 sec)\n ', 'utf8', 'utf8_general_ci', 'utf8_general_ci', _binary ),
	('sys', 'ps_is_instrument_default_timed', 'FUNCTION', 'ps_is_instrument_default_timed', 'SQL', 'READS_SQL_DATA', 'YES', 'INVOKER', _binary 0x20696e5f696e737472756d656e742056415243484152283132382920, _binary 0x656e756d2827594553272c274e4f272920434841525345542075746638, _binary , 'mysql.sys@localhost', '2021-12-25 09:51:02', '2021-12-25 09:51:02', '', '\n Description\n \n Returns whether an instrument is timed by default in this version of MySQL.\n \n Parameters\n \n in_instrument VARCHAR(128): \n The instrument to check.\n \n Returns\n \n ENUM(\'YES\', \'NO\')\n \n Example\n \n mysql> SELECT sys.ps_is_instrument_default_timed(\'statement/sql/select\');\n +------------------------------------------------------------+\n | sys.ps_is_instrument_default_timed(\'statement/sql/select\') |\n +------------------------------------------------------------+\n | YES                                                        |\n +------------------------------------------------------------+\n 1 row in set (0.00 sec)\n ', 'utf8', 'utf8_general_ci', 'utf8_general_ci', _binary ),
	('sys', 'ps_is_thread_instrumented', 'FUNCTION', 'ps_is_thread_instrumented', 'SQL', 'READS_SQL_DATA', 'NO', 'INVOKER', _binary 0x20696e5f636f6e6e656374696f6e5f696420424947494e5420554e5349474e454420, _binary 0x656e756d2827594553272c274e4f272c27554e4b4e4f574e272920434841525345542075746638, _binary , 'mysql.sys@localhost', '2021-12-25 09:51:02', '2021-12-25 09:51:02', '', '\n Description\n \n Checks whether the provided connection id is instrumented within Performance Schema.\n \n Parameters\n \n in_connection_id (BIGINT UNSIGNED):\n The id of the connection to check.\n \n Returns\n \n ENUM(\'YES\', \'NO\', \'UNKNOWN\')\n \n Example\n \n mysql> SELECT sys.ps_is_thread_instrumented(CONNECTION_ID());\n +------------------------------------------------+\n | sys.ps_is_thread_instrumented(CONNECTION_ID()) |\n +------------------------------------------------+\n | YES                                            |\n +------------------------------------------------+\n ', 'utf8', 'utf8_general_ci', 'utf8_general_ci', _binary ),
	('sys', 'ps_thread_id', 'FUNCTION', 'ps_thread_id', 'SQL', 'READS_SQL_DATA', 'NO', 'INVOKER', _binary 0x20696e5f636f6e6e656374696f6e5f696420424947494e5420554e5349474e454420, _binary 0x626967696e742832302920756e7369676e6564, _binary 0x424547494e2052455455524e202853454c454354205448524541445f49442046524f4d2060706572666f726d616e63655f736368656d61602e6074687265616473602057484552452050524f434553534c4953545f4944203d2049464e554c4c28696e5f636f6e6e656374696f6e5f69642c20434f4e4e454354494f4e5f494428292920293b20454e44, 'mysql.sys@localhost', '2021-12-25 09:51:02', '2021-12-25 09:51:02', '', '\n Description\n \n Return the Performance Schema THREAD_ID for the specified connection ID.\n \n Parameters\n \n in_connection_id (BIGINT UNSIGNED):\n The id of the connection to return the thread id for. If NULL, the current\n connection thread id is returned.\n \n Example\n \n mysql> SELECT sys.ps_thread_id(79);\n +----------------------+\n | sys.ps_thread_id(79) |\n +----------------------+\n |                   98 |\n +----------------------+\n 1 row in set (0.00 sec)\n \n mysql> SELECT sys.ps_thread_id(CONNECTION_ID());\n +-----------------------------------+\n | sys.ps_thread_id(CONNECTION_ID()) |\n +-----------------------------------+\n |                                98 |\n +-----------------------------------+\n 1 row in set (0.00 sec)\n ', 'utf8', 'utf8_general_ci', 'utf8_general_ci', _binary 0x424547494e2052455455524e202853454c454354205448524541445f49442046524f4d2060706572666f726d616e63655f736368656d61602e6074687265616473602057484552452050524f434553534c4953545f4944203d2049464e554c4c28696e5f636f6e6e656374696f6e5f69642c20434f4e4e454354494f4e5f494428292920293b20454e44),
	('sys', 'ps_thread_account', 'FUNCTION', 'ps_thread_account', 'SQL', 'READS_SQL_DATA', 'NO', 'INVOKER', _binary 0x20696e5f7468726561645f696420424947494e5420554e5349474e454420, _binary 0x7465787420434841525345542075746638, _binary , 'mysql.sys@localhost', '2021-12-25 09:51:02', '2021-12-25 09:51:02', '', '\n Description\n \n Return the user@host account for the given Performance Schema thread id.\n \n Parameters\n \n in_thread_id (BIGINT UNSIGNED):\n The id of the thread to return the account for.\n \n Example\n \n mysql> select thread_id, processlist_user, processlist_host from performance_schema.threads where type = \'foreground\';\n +-----------+------------------+------------------+\n | thread_id | processlist_user | processlist_host |\n +-----------+------------------+------------------+\n |        23 | NULL             | NULL             |\n |        30 | root             | localhost        |\n |        31 | msandbox         | localhost        |\n |        32 | msandbox         | localhost        |\n +-----------+------------------+------------------+\n 4 rows in set (0.00 sec)\n \n mysql> select sys.ps_thread_account(31);\n +---------------------------+\n | sys.ps_thread_account(31) |\n +---------------------------+\n | msandbox@localhost        |\n +---------------------------+\n 1 row in set (0.00 sec)\n ', 'utf8', 'utf8_general_ci', 'utf8_general_ci', _binary ),
	('sys', 'ps_thread_stack', 'FUNCTION', 'ps_thread_stack', 'SQL', 'READS_SQL_DATA', 'NO', 'INVOKER', _binary 0x207468645f696420424947494e5420554e5349474e45442c20646562756720424f4f4c45414e20, _binary 0x6c6f6e67746578742043484152534554206c6174696e31, _binary , 'mysql.sys@localhost', '2021-12-25 09:51:02', '2021-12-25 09:51:02', '', '\n Description\n \n Outputs a JSON formatted stack of all statements, stages and events\n within Performance Schema for the specified thread.\n \n Parameters\n \n thd_id (BIGINT UNSIGNED):\n The id of the thread to trace. This should match the thread_id\n column from the performance_schema.threads table.\n in_verbose (BOOLEAN):\n Include file:lineno information in the events.\n \n Example\n \n (line separation added for output)\n \n mysql> SELECT sys.ps_thread_stack(37, FALSE) AS thread_stack\\G\n *************************** 1. row ***************************\n thread_stack: {"rankdir": "LR","nodesep": "0.10","stack_created": "2014-02-19 13:39:03",\n "mysql_version": "5.7.3-m13","mysql_user": "root@localhost","events": \n [{"nesting_event_id": "0", "event_id": "10", "timer_wait": 256.35, "event_info": \n "sql/select", "wait_info": "select @@version_comment limit 1\\nerrors: 0\\nwarnings: 0\\nlock time:\n ...\n ', 'utf8', 'utf8_general_ci', 'utf8_general_ci', _binary ),
	('sys', 'ps_thread_trx_info', 'FUNCTION', 'ps_thread_trx_info', 'SQL', 'READS_SQL_DATA', 'NO', 'INVOKER', _binary 0x20696e5f7468726561645f696420424947494e5420554e5349474e454420, _binary 0x6c6f6e677465787420434841525345542075746638, _binary , 'mysql.sys@localhost', '2021-12-25 09:51:02', '2021-12-25 09:51:02', '', '\n Description\n \n Returns a JSON object with info on the given threads current transaction, \n and the statements it has already executed, derived from the\n performance_schema.events_transactions_current and\n performance_schema.events_statements_history tables (so the consumers \n for these also have to be enabled within Performance Schema to get full\n data in the object).\n \n When the output exceeds the default truncation length (65535), a JSON error\n object is returned, such as:\n \n { "error": "Trx info truncated: Row 6 was cut by GROUP_CONCAT()" }\n \n Similar error objects are returned for other warnings/and exceptions raised\n when calling the function.\n \n The max length of the output of this function can be controlled with the\n ps_thread_trx_info.max_length variable set via sys_config, or the\n @sys.ps_thread_trx_info.max_length user variable, as appropriate.\n \n Parameters\n \n in_thread_id (BIGINT UNSIGNED):\n The id of the thread to return the transaction info for.\n \n Example\n \n SELECT sys.ps_thread_trx_info(48)\\G\n *************************** 1. row ***************************\n sys.ps_thread_trx_info(48): [\n {\n "time": "790.70 us",\n "state": "COMMITTED",\n "mode": "READ WRITE",\n "autocommitted": "NO",\n "gtid": "AUTOMATIC",\n "isolation": "REPEATABLE READ",\n "statements_executed": [\n {\n "sql_text": "INSERT INTO info VALUES (1, \'foo\')",\n "time": "471.02 us",\n "schema": "trx",\n "rows_examined": 0,\n "rows_affected": 1,\n "rows_sent": 0,\n "tmp_tables": 0,\n "tmp_disk_tables": 0,\n "sort_rows": 0,\n "sort_merge_passes": 0\n },\n {\n "sql_text": "COMMIT",\n "time": "254.42 us",\n "schema": "trx",\n "rows_examined": 0,\n "rows_affected": 0,\n "rows_sent": 0,\n "tmp_tables": 0,\n "tmp_disk_tables": 0,\n "sort_rows": 0,\n "sort_merge_passes": 0\n }\n ]\n },\n {\n "time": "426.20 us",\n "state": "COMMITTED",\n "mode": "READ WRITE",\n "autocommitted": "NO",\n "gtid": "AUTOMATIC",\n "isolation": "REPEATABLE READ",\n "statements_executed": [\n {\n "sql_text": "INSERT INTO info VALUES (2, \'bar\')",\n "time": "107.33 us",\n "schema": "trx",\n "rows_examined": 0,\n "rows_affected": 1,\n "rows_sent": 0,\n "tmp_tables": 0,\n "tmp_disk_tables": 0,\n "sort_rows": 0,\n "sort_merge_passes": 0\n },\n {\n "sql_text": "COMMIT",\n "time": "213.23 us",\n "schema": "trx",\n "rows_examined": 0,\n "rows_affected": 0,\n "rows_sent": 0,\n "tmp_tables": 0,\n "tmp_disk_tables": 0,\n "sort_rows": 0,\n "sort_merge_passes": 0\n }\n ]\n }\n ]\n 1 row in set (0.03 sec)\n ', 'utf8', 'utf8_general_ci', 'utf8_general_ci', _binary ),
	('sys', 'quote_identifier', 'FUNCTION', 'quote_identifier', 'SQL', 'NO_SQL', 'YES', 'INVOKER', _binary 0x696e5f6964656e7469666965722054455854, _binary 0x7465787420434841525345542075746638, _binary 0x424547494e2052455455524e20434f4e434154282760272c205245504c41434528696e5f6964656e7469666965722c202760272c2027606027292c20276027293b20454e44, 'mysql.sys@localhost', '2021-12-25 09:51:02', '2021-12-25 09:51:02', '', '\n Description\n \n Takes an unquoted identifier (schema name, table name, etc.) and\n returns the identifier quoted with backticks.\n \n Parameters\n \n in_identifier (TEXT):\n The identifier to quote.\n \n Returns\n \n TEXT\n \n Example\n \n mysql> SELECT sys.quote_identifier(\'my_identifier\') AS Identifier;\n +-----------------+\n | Identifier      |\n +-----------------+\n | `my_identifier` |\n +-----------------+\n 1 row in set (0.00 sec)\n \n mysql> SELECT sys.quote_identifier(\'my`idenfier\') AS Identifier;\n +----------------+\n | Identifier     |\n +----------------+\n | `my``idenfier` |\n +----------------+\n 1 row in set (0.00 sec)\n ', 'utf8', 'utf8_general_ci', 'utf8_general_ci', _binary 0x424547494e2052455455524e20434f4e434154282760272c205245504c41434528696e5f6964656e7469666965722c202760272c2027606027292c20276027293b20454e44),
	('sys', 'sys_get_config', 'FUNCTION', 'sys_get_config', 'SQL', 'READS_SQL_DATA', 'YES', 'INVOKER', _binary 0x20696e5f7661726961626c655f6e616d65205641524348415228313238292c20696e5f64656661756c745f76616c75652056415243484152283132382920, _binary 0x76617263686172283132382920434841525345542075746638, _binary , 'mysql.sys@localhost', '2021-12-25 09:51:02', '2021-12-25 09:51:02', '', '\n Description\n \n Returns the value for the requested variable using the following logic:\n \n 1. If the option exists in sys.sys_config return the value from there.\n 2. Else fall back on the provided default value.\n \n Notes for using sys_get_config():\n \n * If the default value argument to sys_get_config() is NULL and case 2. is reached, NULL is returned.\n It is then expected that the caller is able to handle NULL for the given configuration option.\n * The convention is to name the user variables @sys.<name of variable>. It is <name of variable> that\n is stored in the sys_config table and is what is expected as the argument to sys_get_config().\n * If you want to check whether the configuration option has already been set and if not assign with\n the return value of sys_get_config() you can use IFNULL(...) (see example below). However this should\n not be done inside a loop (e.g. for each row in a result set) as for repeated calls where assignment\n is only needed in the first iteration using IFNULL(...) is expected to be significantly slower than\n using an IF (...) THEN ... END IF; block (see example below).\n \n Parameters\n \n in_variable_name (VARCHAR(128)):\n The name of the config option to return the value for.\n \n in_default_value (VARCHAR(128)):\n The default value to return if the variable does not exist in sys.sys_config.\n \n Returns\n \n VARCHAR(128)\n \n Example\n \n mysql> SELECT sys.sys_get_config(\'statement_truncate_len\', 128) AS Value;\n +-------+\n | Value |\n +-------+\n | 64    |\n +-------+\n 1 row in set (0.00 sec)\n \n mysql> SET @sys.statement_truncate_len = IFNULL(@sys.statement_truncate_len, sys.sys_get_config(\'statement_truncate_len\', 64));\n Query OK, 0 rows affected (0.00 sec)\n \n IF (@sys.statement_truncate_len IS NULL) THEN\n SET @sys.statement_truncate_len = sys.sys_get_config(\'statement_truncate_len\', 64);\n END IF;\n ', 'utf8', 'utf8_general_ci', 'utf8_general_ci', _binary ),
	('sys', 'version_major', 'FUNCTION', 'version_major', 'SQL', 'NO_SQL', 'NO', 'INVOKER', _binary '', _binary 0x74696e79696e7428332920756e7369676e6564, _binary 0x424547494e2052455455524e20535542535452494e475f494e44455828535542535452494e475f494e4445582856455253494f4e28292c20272d272c2031292c20272e272c2031293b20454e44, 'mysql.sys@localhost', '2021-12-25 09:51:02', '2021-12-25 09:51:02', '', '\n Description\n \n Returns the major version of MySQL Server.\n \n Returns\n \n TINYINT UNSIGNED\n \n Example\n \n mysql> SELECT VERSION(), sys.version_major();\n +--------------------------------------+---------------------+\n | VERSION()                            | sys.version_major() |\n +--------------------------------------+---------------------+\n | 5.7.9-enterprise-commercial-advanced | 5                   |\n +--------------------------------------+---------------------+\n 1 row in set (0.00 sec)\n ', 'utf8', 'utf8_general_ci', 'utf8_general_ci', _binary 0x424547494e2052455455524e20535542535452494e475f494e44455828535542535452494e475f494e4445582856455253494f4e28292c20272d272c2031292c20272e272c2031293b20454e44),
	('sys', 'version_minor', 'FUNCTION', 'version_minor', 'SQL', 'NO_SQL', 'NO', 'INVOKER', _binary '', _binary 0x74696e79696e7428332920756e7369676e6564, _binary 0x424547494e2052455455524e20535542535452494e475f494e44455828535542535452494e475f494e44455828535542535452494e475f494e4445582856455253494f4e28292c20272d272c2031292c20272e272c2032292c20272e272c202d31293b20454e44, 'mysql.sys@localhost', '2021-12-25 09:51:02', '2021-12-25 09:51:02', '', '\n Description\n \n Returns the minor (release series) version of MySQL Server.\n \n Returns\n \n TINYINT UNSIGNED\n \n Example\n \n mysql> SELECT VERSION(), sys.server_minor();\n +--------------------------------------+---------------------+\n | VERSION()                            | sys.version_minor() |\n +--------------------------------------+---------------------+\n | 5.7.9-enterprise-commercial-advanced | 7                   |\n +--------------------------------------+---------------------+\n 1 row in set (0.00 sec)\n ', 'utf8', 'utf8_general_ci', 'utf8_general_ci', _binary 0x424547494e2052455455524e20535542535452494e475f494e44455828535542535452494e475f494e44455828535542535452494e475f494e4445582856455253494f4e28292c20272d272c2031292c20272e272c2032292c20272e272c202d31293b20454e44),
	('sys', 'version_patch', 'FUNCTION', 'version_patch', 'SQL', 'NO_SQL', 'NO', 'INVOKER', _binary '', _binary 0x74696e79696e7428332920756e7369676e6564, _binary 0x424547494e2052455455524e20535542535452494e475f494e44455828535542535452494e475f494e4445582856455253494f4e28292c20272d272c2031292c20272e272c202d31293b20454e44, 'mysql.sys@localhost', '2021-12-25 09:51:02', '2021-12-25 09:51:02', '', '\n Description\n \n Returns the patch release version of MySQL Server.\n \n Returns\n \n TINYINT UNSIGNED\n \n Example\n \n mysql> SELECT VERSION(), sys.version_patch();\n +--------------------------------------+---------------------+\n | VERSION()                            | sys.version_patch() |\n +--------------------------------------+---------------------+\n | 5.7.9-enterprise-commercial-advanced | 9                   |\n +--------------------------------------+---------------------+\n 1 row in set (0.00 sec)\n ', 'utf8', 'utf8_general_ci', 'utf8_general_ci', _binary 0x424547494e2052455455524e20535542535452494e475f494e44455828535542535452494e475f494e4445582856455253494f4e28292c20272d272c2031292c20272e272c202d31293b20454e44),
	('sys', 'create_synonym_db', 'PROCEDURE', 'create_synonym_db', 'SQL', 'MODIFIES_SQL_DATA', 'NO', 'INVOKER', _binary 0x20494e20696e5f64625f6e616d652056415243484152283634292c2020494e20696e5f73796e6f6e796d20564152434841522836342920, _binary '', _binary , 'mysql.sys@localhost', '2021-12-25 09:51:02', '2021-12-25 09:51:02', '', '\n Description\n \n Takes a source database name and synonym name, and then creates the \n synonym database with views that point to all of the tables within\n the source database.\n \n Useful for creating a "ps" synonym for "performance_schema",\n or "is" instead of "information_schema", for example.\n \n Parameters\n \n in_db_name (VARCHAR(64)):\n The database name that you would like to create a synonym for.\n in_synonym (VARCHAR(64)):\n The database synonym name.\n \n Example\n \n mysql> SHOW DATABASES;\n +--------------------+\n | Database           |\n +--------------------+\n | information_schema |\n | mysql              |\n | performance_schema |\n | sys                |\n | test               |\n +--------------------+\n 5 rows in set (0.00 sec)\n \n mysql> CALL sys.create_synonym_db(\'performance_schema\', \'ps\');\n +---------------------------------------+\n | summary                               |\n +---------------------------------------+\n | Created 74 views in the `ps` database |\n +---------------------------------------+\n 1 row in set (8.57 sec)\n \n Query OK, 0 rows affected (8.57 sec)\n \n mysql> SHOW DATABASES;\n +--------------------+\n | Database           |\n +--------------------+\n | information_schema |\n | mysql              |\n | performance_schema |\n | ps                 |\n | sys                |\n | test               |\n +--------------------+\n 6 rows in set (0.00 sec)\n \n mysql> SHOW FULL TABLES FROM ps;\n +------------------------------------------------------+------------+\n | Tables_in_ps                                         | Table_type |\n +------------------------------------------------------+------------+\n | accounts                                             | VIEW       |\n | cond_instances                                       | VIEW       |\n | events_stages_current                                | VIEW       |\n | events_stages_history                                | VIEW       |\n ...\n ', 'utf8', 'utf8_general_ci', 'utf8_general_ci', _binary ),
	('sys', 'execute_prepared_stmt', 'PROCEDURE', 'execute_prepared_stmt', 'SQL', 'READS_SQL_DATA', 'NO', 'INVOKER', _binary 0x20494e20696e5f7175657279206c6f6e67746578742043484152414354455220534554205554463820, _binary '', _binary , 'mysql.sys@localhost', '2021-12-25 09:51:02', '2021-12-25 09:51:02', '', '\n Description\n \n Takes the query in the argument and executes it using a prepared statement. The prepared statement is deallocated,\n so the procedure is mainly useful for executing one off dynamically created queries.\n \n The sys_execute_prepared_stmt prepared statement name is used for the query and is required not to exist.\n \n \n Parameters\n \n in_query (longtext CHARACTER SET UTF8):\n The query to execute.\n \n \n Configuration Options\n \n sys.debug\n Whether to provide debugging output.\n Default is \'OFF\'. Set to \'ON\' to include.\n \n \n Example\n \n mysql> CALL sys.execute_prepared_stmt(\'SELECT * FROM sys.sys_config\');\n +------------------------+-------+---------------------+--------+\n | variable               | value | set_time            | set_by |\n +------------------------+-------+---------------------+--------+\n | statement_truncate_len | 64    | 2015-06-30 13:06:00 | NULL   |\n +------------------------+-------+---------------------+--------+\n 1 row in set (0.00 sec)\n \n Query OK, 0 rows affected (0.00 sec)\n ', 'utf8', 'utf8_general_ci', 'utf8_general_ci', _binary ),
	('sys', 'diagnostics', 'PROCEDURE', 'diagnostics', 'SQL', 'READS_SQL_DATA', 'NO', 'INVOKER', _binary 0x20494e20696e5f6d61785f72756e74696d6520696e7420756e7369676e65642c20494e20696e5f696e74657276616c20696e7420756e7369676e65642c20494e20696e5f6175746f5f636f6e66696720656e756d20282763757272656e74272c20276d656469756d272c202766756c6c272920, _binary '', _binary , 'mysql.sys@localhost', '2021-12-25 09:51:02', '2021-12-25 09:51:02', '', '\n Description\n \n Create a report of the current status of the server for diagnostics purposes. Data collected includes (some items depends on versions and settings):\n \n * The GLOBAL VARIABLES\n * Several sys schema views including metrics or equivalent (depending on version and settings)\n * Queries in the 95th percentile\n * Several ndbinfo views for MySQL Cluster\n * Replication (both master and slave) information.\n \n Some of the sys schema views are calculated as initial (optional), overall, delta:\n \n * The initial view is the content of the view at the start of this procedure.\n This output will be the same as the the start values used for the delta view.\n The initial view is included if @sys.diagnostics.include_raw = \'ON\'.\n * The overall view is the content of the view at the end of this procedure.\n This output is the same as the end values used for the delta view.\n The overall view is always included.\n * The delta view is the difference from the beginning to the end. Note that for min and max values\n they are simply the min or max value from the end view respectively, so does not necessarily reflect\n the minimum/maximum value in the monitored period.\n Note: except for the metrics views the delta is only calculation between the first and last outputs.\n \n Requires the SUPER privilege for "SET sql_log_bin = 0;".\n \n Versions supported:\n * MySQL 5.6: 5.6.10 and later\n * MySQL 5.7: 5.7.9 and later\n \n Parameters\n \n in_max_runtime (INT UNSIGNED):\n The maximum time to keep collecting data.\n Use NULL to get the default which is 60 seconds, otherwise enter a value greater than 0.\n in_interval (INT UNSIGNED):\n How long to sleep between data collections.\n Use NULL to get the default which is 30 seconds, otherwise enter a value greater than 0.\n in_auto_config (ENUM(\'current\', \'medium\', \'full\'))\n Automatically enable Performance Schema instruments and consumers.\n NOTE: The more that are enabled, the more impact on the performance.\n Supported values are:\n * current - use the current settings.\n * medium - enable some settings.\n * full - enables all settings. This will have a big impact on the\n performance - be careful using this option.\n If another setting the \'current\' is chosen, the current settings\n are restored at the end of the procedure.\n \n \n Configuration Options\n \n sys.diagnostics.allow_i_s_tables\n Specifies whether it is allowed to do table scan queries on information_schema.TABLES. This can be expensive if there\n are many tables. Set to \'ON\' to allow, \'OFF\' to not allow.\n Default is \'OFF\'.\n \n sys.diagnostics.include_raw\n Set to \'ON\' to include the raw data (e.g. the original output of "SELECT * FROM sys.metrics").\n Use this to get the initial values of the various views.\n Default is \'OFF\'.\n \n sys.statement_truncate_len\n How much of queries in the process list output to include.\n Default is 64.\n \n sys.debug\n Whether to provide debugging output.\n Default is \'OFF\'. Set to \'ON\' to include.\n \n \n Example\n \n To create a report and append it to the file diag.out:\n \n mysql> TEE diag.out;\n mysql> CALL sys.diagnostics(120, 30, \'current\');\n ...\n mysql> NOTEE;\n ', 'utf8', 'utf8_general_ci', 'utf8_general_ci', _binary ),
	('sys', 'ps_statement_avg_latency_histogram', 'PROCEDURE', 'ps_statement_avg_latency_histogram', 'SQL', 'READS_SQL_DATA', 'NO', 'INVOKER', _binary '', _binary '', _binary , 'mysql.sys@localhost', '2021-12-25 09:51:02', '2021-12-25 09:51:02', '', '\n Description\n \n Outputs a textual histogram graph of the average latency values\n across all normalized queries tracked within the Performance Schema\n events_statements_summary_by_digest table.\n \n Can be used to show a very high level picture of what kind of \n latency distribution statements running within this instance have.\n \n Parameters\n \n None.\n \n Example\n \n mysql> CALL sys.ps_statement_avg_latency_histogram()\\G\n *************************** 1. row ***************************\n Performance Schema Statement Digest Average Latency Histogram:\n \n . = 1 unit\n * = 2 units\n # = 3 units\n \n (0 - 38ms)     240 | ################################################################################\n (38 - 77ms)    38  | ......................................\n (77 - 115ms)   3   | ...\n (115 - 154ms)  62  | *******************************\n (154 - 192ms)  3   | ...\n (192 - 231ms)  0   |\n (231 - 269ms)  0   |\n (269 - 307ms)  0   |\n (307 - 346ms)  0   |\n (346 - 384ms)  1   | .\n (384 - 423ms)  1   | .\n (423 - 461ms)  0   |\n (461 - 499ms)  0   |\n (499 - 538ms)  0   |\n (538 - 576ms)  0   |\n (576 - 615ms)  1   | .\n \n Total Statements: 350; Buckets: 16; Bucket Size: 38 ms;\n ', 'utf8', 'utf8_general_ci', 'utf8_general_ci', _binary ),
	('sys', 'ps_trace_statement_digest', 'PROCEDURE', 'ps_trace_statement_digest', 'SQL', 'MODIFIES_SQL_DATA', 'NO', 'INVOKER', _binary 0x20494e20696e5f6469676573742056415243484152283332292c20494e20696e5f72756e74696d6520494e542c20494e20696e5f696e74657276616c20444543494d414c28322c32292c20494e20696e5f73746172745f667265736820424f4f4c45414e2c20494e20696e5f6175746f5f656e61626c6520424f4f4c45414e20, _binary '', _binary , 'mysql.sys@localhost', '2021-12-25 09:51:02', '2021-12-25 09:51:02', '', '\n Description\n \n Traces all instrumentation within Performance Schema for a specific\n Statement Digest.\n \n When finding a statement of interest within the\n performance_schema.events_statements_summary_by_digest table, feed\n the DIGEST MD5 value in to this procedure, set how long to poll for,\n and at what interval to poll, and it will generate a report of all\n statistics tracked within Performance Schema for that digest for the\n interval.\n \n It will also attempt to generate an EXPLAIN for the longest running\n example of the digest during the interval. Note this may fail, as:\n \n * Performance Schema truncates long SQL_TEXT values (and hence the\n EXPLAIN will fail due to parse errors)\n * the default schema is sys (so tables that are not fully qualified\n in the query may not be found)\n * some queries such as SHOW are not supported in EXPLAIN.\n \n When the EXPLAIN fails, the error will be ignored and no EXPLAIN\n output generated.\n \n Requires the SUPER privilege for "SET sql_log_bin = 0;".\n \n Parameters\n \n in_digest (VARCHAR(32)):\n The statement digest identifier you would like to analyze\n in_runtime (INT):\n The number of seconds to run analysis for\n in_interval (DECIMAL(2,2)):\n The interval (in seconds, may be fractional) at which to try\n and take snapshots\n in_start_fresh (BOOLEAN):\n Whether to TRUNCATE the events_statements_history_long and\n events_stages_history_long tables before starting\n in_auto_enable (BOOLEAN):\n Whether to automatically turn on required consumers\n \n Example\n \n mysql> call ps_trace_statement_digest(\'891ec6860f98ba46d89dd20b0c03652c\', 10, 0.1, true, true);\n +--------------------+\n | SUMMARY STATISTICS |\n +--------------------+\n | SUMMARY STATISTICS |\n +--------------------+\n 1 row in set (9.11 sec)\n \n +------------+-----------+-----------+-----------+---------------+------------+------------+\n | executions | exec_time | lock_time | rows_sent | rows_examined | tmp_tables | full_scans |\n +------------+-----------+-----------+-----------+---------------+------------+------------+\n |         21 | 4.11 ms   | 2.00 ms   |         0 |            21 |          0 |          0 |\n +------------+-----------+-----------+-----------+---------------+------------+------------+\n 1 row in set (9.11 sec)\n \n +------------------------------------------+-------+-----------+\n | event_name                               | count | latency   |\n +------------------------------------------+-------+-----------+\n | stage/sql/checking query cache for query |    16 | 724.37 us |\n | stage/sql/statistics                     |    16 | 546.92 us |\n | stage/sql/freeing items                  |    18 | 520.11 us |\n | stage/sql/init                           |    51 | 466.80 us |\n ...\n | stage/sql/cleaning up                    |    18 | 11.92 us  |\n | stage/sql/executing                      |    16 | 6.95 us   |\n +------------------------------------------+-------+-----------+\n 17 rows in set (9.12 sec)\n \n +---------------------------+\n | LONGEST RUNNING STATEMENT |\n +---------------------------+\n | LONGEST RUNNING STATEMENT |\n +---------------------------+\n 1 row in set (9.16 sec)\n \n +-----------+-----------+-----------+-----------+---------------+------------+-----------+\n | thread_id | exec_time | lock_time | rows_sent | rows_examined | tmp_tables | full_scan |\n +-----------+-----------+-----------+-----------+---------------+------------+-----------+\n |    166646 | 618.43 us | 1.00 ms   |         0 |             1 |          0 |         0 |\n +-----------+-----------+-----------+-----------+---------------+------------+-----------+\n 1 row in set (9.16 sec)\n \n // Truncated for clarity...\n +-----------------------------------------------------------------+\n | sql_text                                                        |\n +-----------------------------------------------------------------+\n | select hibeventhe0_.id as id1382_, hibeventhe0_.createdTime ... |\n +-----------------------------------------------------------------+\n 1 row in set (9.17 sec)\n \n +------------------------------------------+-----------+\n | event_name                               | latency   |\n +------------------------------------------+-----------+\n | stage/sql/init                           | 8.61 us   |\n | stage/sql/Waiting for query cache lock   | 453.23 us |\n | stage/sql/init                           | 331.07 ns |\n | stage/sql/checking query cache for query | 43.04 us  |\n ...\n | stage/sql/freeing items                  | 30.46 us  |\n | stage/sql/cleaning up                    | 662.13 ns |\n +------------------------------------------+-----------+\n 18 rows in set (9.23 sec)\n \n +----+-------------+--------------+-------+---------------+-----------+---------+-------------+------+-------+\n | id | select_type | table        | type  | possible_keys | key       | key_len | ref         | rows | Extra |\n +----+-------------+--------------+-------+---------------+-----------+---------+-------------+------+-------+\n |  1 | SIMPLE      | hibeventhe0_ | const | fixedTime     | fixedTime | 775     | const,const |    1 | NULL  |\n +----+-------------+--------------+-------+---------------+-----------+---------+-------------+------+-------+\n 1 row in set (9.27 sec)\n \n Query OK, 0 rows affected (9.28 sec)\n ', 'utf8', 'utf8_general_ci', 'utf8_general_ci', _binary ),
	('sys', 'ps_trace_thread', 'PROCEDURE', 'ps_trace_thread', 'SQL', 'MODIFIES_SQL_DATA', 'NO', 'INVOKER', _binary , _binary '', _binary , 'mysql.sys@localhost', '2021-12-25 09:51:02', '2021-12-25 09:51:02', '', '\n Description\n \n Dumps all data within Performance Schema for an instrumented thread,\n to create a DOT formatted graph file. \n \n Each resultset returned from the procedure should be used for a complete graph\n \n Requires the SUPER privilege for "SET sql_log_bin = 0;".\n \n Parameters\n \n in_thread_id (BIGINT UNSIGNED):\n The thread that you would like a stack trace for\n in_outfile  (VARCHAR(255)):\n The filename the dot file will be written to\n in_max_runtime (DECIMAL(20,2)):\n The maximum time to keep collecting data.\n Use NULL to get the default which is 60 seconds.\n in_interval (DECIMAL(20,2)): \n How long to sleep between data collections. \n Use NULL to get the default which is 1 second.\n in_start_fresh (BOOLEAN):\n Whether to reset all Performance Schema data before tracing.\n in_auto_setup (BOOLEAN):\n Whether to disable all other threads and enable all consumers/instruments. \n This will also reset the settings at the end of the run.\n in_debug (BOOLEAN):\n Whether you would like to include file:lineno in the graph\n \n Example\n \n mysql> CALL sys.ps_trace_thread(25, CONCAT(\'/tmp/stack-\', REPLACE(NOW(), \' \', \'-\'), \'.dot\'), NULL, NULL, TRUE, TRUE, TRUE);\n +-------------------+\n | summary           |\n +-------------------+\n | Disabled 1 thread |\n +-------------------+\n 1 row in set (0.00 sec)\n \n +---------------------------------------------+\n | Info                                        |\n +---------------------------------------------+\n | Data collection starting for THREAD_ID = 25 |\n +---------------------------------------------+\n 1 row in set (0.03 sec)\n \n +-----------------------------------------------------------+\n | Info                                                      |\n +-----------------------------------------------------------+\n | Stack trace written to /tmp/stack-2014-02-16-21:18:41.dot |\n +-----------------------------------------------------------+\n 1 row in set (60.07 sec)\n \n +-------------------------------------------------------------------+\n | Convert to PDF                                                    |\n +-------------------------------------------------------------------+\n | dot -Tpdf -o /tmp/stack_25.pdf /tmp/stack-2014-02-16-21:18:41.dot |\n +-------------------------------------------------------------------+\n 1 row in set (60.07 sec)\n \n +-------------------------------------------------------------------+\n | Convert to PNG                                                    |\n +-------------------------------------------------------------------+\n | dot -Tpng -o /tmp/stack_25.png /tmp/stack-2014-02-16-21:18:41.dot |\n +-------------------------------------------------------------------+\n 1 row in set (60.07 sec)\n \n +------------------+\n | summary          |\n +------------------+\n | Enabled 1 thread |\n +------------------+\n 1 row in set (60.32 sec)\n ', 'utf8', 'utf8_general_ci', 'utf8_general_ci', _binary ),
	('sys', 'ps_setup_disable_background_threads', 'PROCEDURE', 'ps_setup_disable_background_threads', 'SQL', 'MODIFIES_SQL_DATA', 'NO', 'INVOKER', _binary '', _binary '', _binary , 'mysql.sys@localhost', '2021-12-25 09:51:02', '2021-12-25 09:51:02', '', '\n Description\n \n Disable all background thread instrumentation within Performance Schema.\n \n Parameters\n \n None.\n \n Example\n \n mysql> CALL sys.ps_setup_disable_background_threads();\n +--------------------------------+\n | summary                        |\n +--------------------------------+\n | Disabled 18 background threads |\n +--------------------------------+\n 1 row in set (0.00 sec)\n ', 'utf8', 'utf8_general_ci', 'utf8_general_ci', _binary ),
	('sys', 'ps_setup_disable_consumer', 'PROCEDURE', 'ps_setup_disable_consumer', 'SQL', 'MODIFIES_SQL_DATA', 'NO', 'INVOKER', _binary 0x20494e20636f6e73756d65722056415243484152283132382920, _binary '', _binary , 'mysql.sys@localhost', '2021-12-25 09:51:02', '2021-12-25 09:51:02', '', '\n Description\n \n Disables consumers within Performance Schema \n matching the input pattern.\n \n Parameters\n \n consumer (VARCHAR(128)):\n A LIKE pattern match (using "%consumer%") of consumers to disable\n \n Example\n \n To disable all consumers:\n \n mysql> CALL sys.ps_setup_disable_consumer(\'\');\n +--------------------------+\n | summary                  |\n +--------------------------+\n | Disabled 15 consumers    |\n +--------------------------+\n 1 row in set (0.02 sec)\n \n To disable just the event_stage consumers:\n \n mysql> CALL sys.ps_setup_disable_comsumers(\'stage\');\n +------------------------+\n | summary                |\n +------------------------+\n | Disabled 3 consumers   |\n +------------------------+\n 1 row in set (0.00 sec)\n ', 'utf8', 'utf8_general_ci', 'utf8_general_ci', _binary ),
	('sys', 'ps_setup_disable_instrument', 'PROCEDURE', 'ps_setup_disable_instrument', 'SQL', 'MODIFIES_SQL_DATA', 'NO', 'INVOKER', _binary 0x20494e20696e5f7061747465726e2056415243484152283132382920, _binary '', _binary , 'mysql.sys@localhost', '2021-12-25 09:51:02', '2021-12-25 09:51:02', '', '\n Description\n \n Disables instruments within Performance Schema \n matching the input pattern.\n \n Parameters\n \n in_pattern (VARCHAR(128)):\n A LIKE pattern match (using "%in_pattern%") of events to disable\n \n Example\n \n To disable all mutex instruments:\n \n mysql> CALL sys.ps_setup_disable_instrument(\'wait/synch/mutex\');\n +--------------------------+\n | summary                  |\n +--------------------------+\n | Disabled 155 instruments |\n +--------------------------+\n 1 row in set (0.02 sec)\n \n To disable just a specific TCP/IP based network IO instrument:\n \n mysql> CALL sys.ps_setup_disable_instrument(\'wait/io/socket/sql/server_tcpip_socket\');\n +------------------------+\n | summary                |\n +------------------------+\n | Disabled 1 instruments |\n +------------------------+\n 1 row in set (0.00 sec)\n \n To disable all instruments:\n \n mysql> CALL sys.ps_setup_disable_instrument(\'\');\n +--------------------------+\n | summary                  |\n +--------------------------+\n | Disabled 547 instruments |\n +--------------------------+\n 1 row in set (0.01 sec)\n ', 'utf8', 'utf8_general_ci', 'utf8_general_ci', _binary ),
	('sys', 'ps_setup_disable_thread', 'PROCEDURE', 'ps_setup_disable_thread', 'SQL', 'MODIFIES_SQL_DATA', 'NO', 'INVOKER', _binary 0x20494e20696e5f636f6e6e656374696f6e5f696420424947494e5420, _binary '', _binary , 'mysql.sys@localhost', '2021-12-25 09:51:02', '2021-12-25 09:51:02', '', '\n Description\n \n Disable the given connection/thread in Performance Schema.\n \n Parameters\n \n in_connection_id (BIGINT):\n The connection ID (PROCESSLIST_ID from performance_schema.threads\n or the ID shown within SHOW PROCESSLIST)\n \n Example\n \n mysql> CALL sys.ps_setup_disable_thread(3);\n +-------------------+\n | summary           |\n +-------------------+\n | Disabled 1 thread |\n +-------------------+\n 1 row in set (0.01 sec)\n \n To disable the current connection:\n \n mysql> CALL sys.ps_setup_disable_thread(CONNECTION_ID());\n +-------------------+\n | summary           |\n +-------------------+\n | Disabled 1 thread |\n +-------------------+\n 1 row in set (0.00 sec)\n ', 'utf8', 'utf8_general_ci', 'utf8_general_ci', _binary ),
	('sys', 'ps_setup_enable_background_threads', 'PROCEDURE', 'ps_setup_enable_background_threads', 'SQL', 'MODIFIES_SQL_DATA', 'NO', 'INVOKER', _binary '', _binary '', _binary , 'mysql.sys@localhost', '2021-12-25 09:51:02', '2021-12-25 09:51:02', '', '\n Description\n \n Enable all background thread instrumentation within Performance Schema.\n \n Parameters\n \n None.\n \n Example\n \n mysql> CALL sys.ps_setup_enable_background_threads();\n +-------------------------------+\n | summary                       |\n +-------------------------------+\n | Enabled 18 background threads |\n +-------------------------------+\n 1 row in set (0.00 sec)\n ', 'utf8', 'utf8_general_ci', 'utf8_general_ci', _binary ),
	('sys', 'ps_setup_enable_consumer', 'PROCEDURE', 'ps_setup_enable_consumer', 'SQL', 'MODIFIES_SQL_DATA', 'NO', 'INVOKER', _binary 0x20494e20636f6e73756d65722056415243484152283132382920, _binary '', _binary , 'mysql.sys@localhost', '2021-12-25 09:51:02', '2021-12-25 09:51:02', '', '\n Description\n \n Enables consumers within Performance Schema \n matching the input pattern.\n \n Parameters\n \n consumer (VARCHAR(128)):\n A LIKE pattern match (using "%consumer%") of consumers to enable\n \n Example\n \n To enable all consumers:\n \n mysql> CALL sys.ps_setup_enable_consumer(\'\');\n +-------------------------+\n | summary                 |\n +-------------------------+\n | Enabled 10 consumers    |\n +-------------------------+\n 1 row in set (0.02 sec)\n \n Query OK, 0 rows affected (0.02 sec)\n \n To enable just "waits" consumers:\n \n mysql> CALL sys.ps_setup_enable_consumer(\'waits\');\n +-----------------------+\n | summary               |\n +-----------------------+\n | Enabled 3 consumers   |\n +-----------------------+\n 1 row in set (0.00 sec)\n \n Query OK, 0 rows affected (0.00 sec)\n ', 'utf8', 'utf8_general_ci', 'utf8_general_ci', _binary ),
	('sys', 'ps_setup_enable_instrument', 'PROCEDURE', 'ps_setup_enable_instrument', 'SQL', 'MODIFIES_SQL_DATA', 'NO', 'INVOKER', _binary 0x20494e20696e5f7061747465726e2056415243484152283132382920, _binary '', _binary , 'mysql.sys@localhost', '2021-12-25 09:51:02', '2021-12-25 09:51:02', '', '\n Description\n \n Enables instruments within Performance Schema \n matching the input pattern.\n \n Parameters\n \n in_pattern (VARCHAR(128)):\n A LIKE pattern match (using "%in_pattern%") of events to enable\n \n Example\n \n To enable all mutex instruments:\n \n mysql> CALL sys.ps_setup_enable_instrument(\'wait/synch/mutex\');\n +-------------------------+\n | summary                 |\n +-------------------------+\n | Enabled 155 instruments |\n +-------------------------+\n 1 row in set (0.02 sec)\n \n Query OK, 0 rows affected (0.02 sec)\n \n To enable just a specific TCP/IP based network IO instrument:\n \n mysql> CALL sys.ps_setup_enable_instrument(\'wait/io/socket/sql/server_tcpip_socket\');\n +-----------------------+\n | summary               |\n +-----------------------+\n | Enabled 1 instruments |\n +-----------------------+\n 1 row in set (0.00 sec)\n \n Query OK, 0 rows affected (0.00 sec)\n \n To enable all instruments:\n \n mysql> CALL sys.ps_setup_enable_instrument(\'\');\n +-------------------------+\n | summary                 |\n +-------------------------+\n | Enabled 547 instruments |\n +-------------------------+\n 1 row in set (0.01 sec)\n \n Query OK, 0 rows affected (0.01 sec)\n ', 'utf8', 'utf8_general_ci', 'utf8_general_ci', _binary ),
	('sys', 'ps_setup_enable_thread', 'PROCEDURE', 'ps_setup_enable_thread', 'SQL', 'MODIFIES_SQL_DATA', 'NO', 'INVOKER', _binary 0x20494e20696e5f636f6e6e656374696f6e5f696420424947494e5420, _binary '', _binary , 'mysql.sys@localhost', '2021-12-25 09:51:02', '2021-12-25 09:51:02', '', '\n Description\n \n Enable the given connection/thread in Performance Schema.\n \n Parameters\n \n in_connection_id (BIGINT):\n The connection ID (PROCESSLIST_ID from performance_schema.threads\n or the ID shown within SHOW PROCESSLIST)\n \n Example\n \n mysql> CALL sys.ps_setup_enable_thread(3);\n +------------------+\n | summary          |\n +------------------+\n | Enabled 1 thread |\n +------------------+\n 1 row in set (0.01 sec)\n \n To enable the current connection:\n \n mysql> CALL sys.ps_setup_enable_thread(CONNECTION_ID());\n +------------------+\n | summary          |\n +------------------+\n | Enabled 1 thread |\n +------------------+\n 1 row in set (0.00 sec)\n ', 'utf8', 'utf8_general_ci', 'utf8_general_ci', _binary ),
	('sys', 'ps_setup_reload_saved', 'PROCEDURE', 'ps_setup_reload_saved', 'SQL', 'MODIFIES_SQL_DATA', 'NO', 'INVOKER', _binary '', _binary '', _binary , 'mysql.sys@localhost', '2021-12-25 09:51:02', '2021-12-25 09:51:02', '', '\n Description\n \n Reloads a saved Performance Schema configuration,\n so that you can alter the setup for debugging purposes, \n but restore it to a previous state.\n \n Use the companion procedure - ps_setup_save(), to \n save a configuration.\n \n Requires the SUPER privilege for "SET sql_log_bin = 0;".\n \n Parameters\n \n None.\n \n Example\n \n mysql> CALL sys.ps_setup_save();\n Query OK, 0 rows affected (0.08 sec)\n \n mysql> UPDATE performance_schema.setup_instruments SET enabled = \'YES\', timed = \'YES\';\n Query OK, 547 rows affected (0.40 sec)\n Rows matched: 784  Changed: 547  Warnings: 0\n \n /* Run some tests that need more detailed instrumentation here */\n \n mysql> CALL sys.ps_setup_reload_saved();\n Query OK, 0 rows affected (0.32 sec)\n ', 'utf8', 'utf8_general_ci', 'utf8_general_ci', _binary ),
	('sys', 'ps_setup_reset_to_default', 'PROCEDURE', 'ps_setup_reset_to_default', 'SQL', 'MODIFIES_SQL_DATA', 'NO', 'INVOKER', _binary 0x20494e20696e5f766572626f736520424f4f4c45414e20, _binary '', _binary , 'mysql.sys@localhost', '2021-12-25 09:51:02', '2021-12-25 09:51:02', '', '\n Description\n \n Resets the Performance Schema setup to the default settings.\n \n Parameters\n \n in_verbose (BOOLEAN):\n Whether to print each setup stage (including the SQL) whilst running.\n \n Example\n \n mysql> CALL sys.ps_setup_reset_to_default(true)\\G\n *************************** 1. row ***************************\n status: Resetting: setup_actors\n DELETE\n FROM performance_schema.setup_actors\n WHERE NOT (HOST = \'%\' AND USER = \'%\' AND `ROLE` = \'%\')\n 1 row in set (0.00 sec)\n \n *************************** 1. row ***************************\n status: Resetting: setup_actors\n INSERT IGNORE INTO performance_schema.setup_actors\n VALUES (\'%\', \'%\', \'%\')\n 1 row in set (0.00 sec)\n ...\n \n mysql> CALL sys.ps_setup_reset_to_default(false)\\G\n Query OK, 0 rows affected (0.00 sec)\n ', 'utf8', 'utf8_general_ci', 'utf8_general_ci', _binary ),
	('sys', 'ps_setup_save', 'PROCEDURE', 'ps_setup_save', 'SQL', 'MODIFIES_SQL_DATA', 'NO', 'INVOKER', _binary 0x20494e20696e5f74696d656f757420494e5420, _binary '', _binary , 'mysql.sys@localhost', '2021-12-25 09:51:02', '2021-12-25 09:51:02', '', '\n Description\n \n Saves the current configuration of Performance Schema, \n so that you can alter the setup for debugging purposes, \n but restore it to a previous state.\n \n Use the companion procedure - ps_setup_reload_saved(), to \n restore the saved config.\n \n The named lock "sys.ps_setup_save" is taken before the\n current configuration is saved. If the attempt to get the named\n lock times out, an error occurs.\n \n The lock is released after the settings have been restored by\n calling ps_setup_reload_saved().\n \n Requires the SUPER privilege for "SET sql_log_bin = 0;".\n \n Parameters\n \n in_timeout INT\n The timeout in seconds used when trying to obtain the lock.\n A negative timeout means infinite timeout.\n \n Example\n \n mysql> CALL sys.ps_setup_save(-1);\n Query OK, 0 rows affected (0.08 sec)\n \n mysql> UPDATE performance_schema.setup_instruments \n ->    SET enabled = \'YES\', timed = \'YES\';\n Query OK, 547 rows affected (0.40 sec)\n Rows matched: 784  Changed: 547  Warnings: 0\n \n /* Run some tests that need more detailed instrumentation here */\n \n mysql> CALL sys.ps_setup_reload_saved();\n Query OK, 0 rows affected (0.32 sec)\n ', 'utf8', 'utf8_general_ci', 'utf8_general_ci', _binary ),
